package com.chnghx.web.common.controller;

import java.io.IOException;

import javax.servlet.http.Cookie;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.codehaus.jackson.JsonGenerationException;
import org.codehaus.jackson.map.JsonMappingException;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.ResponseBody;

import com.chnghx.web.common.APIResult;
import com.chnghx.web.common.BaseController;
import com.chnghx.web.common.VCache;



@Controller
@RequestMapping("common")
public class CommonController extends BaseController{

	@RequestMapping("refused")
	@ResponseBody
	public void index(HttpServletRequest request, HttpServletResponse response) throws JsonGenerationException, JsonMappingException, IOException{
		
		HttpSession session = request.getSession(true);
		System.out.println(session);
		
		VCache.put("a", "a100", 1000);
		
		Object t = VCache.get("a");
		System.out.println(t);
		
		
		APIResult<Object> result = new APIResult<Object>();
		result.setStatus("502");
		result.setMessage("拒绝访问");
		result.setResult("访问失败了哦");
		displayJSON(result, response);
	}
	
	
	@RequestMapping("test")
	@ResponseBody
	public void test(HttpServletRequest request, HttpServletResponse response) throws JsonGenerationException, JsonMappingException, IOException{
		
		Cookie cookie1 = new Cookie("d", "[]");
//		Cookie cookie2 = new Cookie("b", "[]");
//		Cookie cookie3 = new Cookie("c", "[]");
//		response.addCookie(cookie1);
//		response.addCookie(cookie2);
		response.addCookie(cookie1);
		
		APIResult<Object> result = new APIResult<Object>();
		result.setStatus("200");
		result.setMessage("测试cookie");
		result.setResult("访问成功");
		displayJSON(result, response);
	}
}
